clear all;%load the datadata = importdata('ml-100k/u.data');user_id = data(:, 1);item_id = data(:, 2);row_num = max(user_id);col_num = max(item_id);R = zeros(row_num, col_num);for i = 1:size(user_id)    R(data(i, 1), data(i, 2)) = data(i, 3);end[A1,Y1] = wnmfrule(R,10);error1 = squareError(R,A1,Y1);[A2,Y2] = wnmfrule(R,50);error2 = squareError(R,A2,Y2);[A3,Y3] = wnmfrule(R,100);error3 = squareError(R,A3,Y3);disp(['k = 10, total least squared error: ' num2str(error1)])disp(['k = 50, total least squared error: ' num2str(error2)])disp(['k = 100, total least squared error: ' num2str(error3)])